var _ = require('lodash');

exports.formatMedias = function(list) {
  return _.map(list, function(item) {
    var _item = {};
    _item.author = item.get('author').toJSON();
    _item.media_cover_url = item.get('media_cover').url();
    _item.media_src = item.get('media_src');
    _item.media_name = item.get('media_name');
    _item.type = item.get('type');
    _item.id = item.id;
    return _item;
  });
};
